def get_clean_filename(file_path: str, correlation_id: str) -> str:
    filename = os.path.basename(file_path)
    if filename.startswith(f"{correlation_id}_"):
        return filename[len(correlation_id) + 1:]
    return filename

def find_pdf_by_correlation_id(correlation_id: str) -> str | None:
    for root, _, files in os.walk(settings.UPLOAD_DIR):
        for f in files:
            if f.startswith(f"{correlation_id}_"):
                full_path = os.path.join(root, f)
                return full_path
    return None

